--[[    Fluent Interface Suite - Mobile Edition (Optimized)    Автор оригинала: dawid    Модификация: Mobile-Only (Удалены Keybinds, адаптирован Scale)]]local UserInputService = game:GetService("UserInputService")local TweenService = game:GetService("TweenService")local RunService = game:GetService("RunService")local Players = game:GetService("Players")local LocalPlayer = Players.LocalPlayerlocal PlayerGui = LocalPlayer:WaitForChild("PlayerGui")local FluentMobile = {    Options = {},    Theme = "Dark",    Elements = {},    Window = nil}-- Вспомогательные функции для UIlocal function Create(class, props)    local obj = Instance.new(class)    for k, v in pairs(props) do        obj[k] = v    end    return objend-- Основная логика создания окнаfunction FluentMobile:CreateWindow(config)    local Title = config.Title or "Fluent Mobile"    local SubTitle = config.SubTitle or "Mobile Optimized"        local ScreenGui = Create("ScreenGui", {        Name = "FluentMobileUI",        Parent = PlayerGui,        ResetOnSpawn = false    })    -- Главный контейнер (используем Scale для адаптивности)    local Main = Create("Frame", {        Name = "Main",        Parent = ScreenGui,        BackgroundColor3 = Color3.fromRGB(30, 30, 30),        Size = UDim2.new(0.9, 0, 0.8, 0), -- 90% ширины, 80% высоты        Position = UDim2.new(0.05, 0, 0.1, 0),        BorderSizePixel = 0    })    Create("UICorner", { CornerRadius = UDim.new(0, 8), Parent = Main })    local Header = Create("Frame", {        Name = "Header",        Parent = Main,        Size = UDim2.new(1, 0, 0.1, 0),        BackgroundTransparency = 1    })    local TitleLabel = Create("TextLabel", {        Parent = Header,        Text = Title .. " | " .. SubTitle,        TextColor3 = Color3.fromRGB(255, 255, 255),        TextScaled = true,        Size = UDim2.new(0.8, 0, 0.6, 0),        Position = UDim2.new(0.05, 0, 0.2, 0),        BackgroundTransparency = 1,        TextXAlignment = Enum.TextXAlignment.Left,        FontFace = Font.fromEnum(Enum.Font.GothamMedium)    })    local Content = Create("ScrollingFrame", {        Name = "Content",        Parent = Main,        Size = UDim2.new(0.95, 0, 0.85, 0),        Position = UDim2.new(0.025, 0, 0.12, 0),        BackgroundTransparency = 1,        ScrollBarThickness = 4,        CanvasSize = UDim2.new(0, 0, 0, 0)    })    Create("UIListLayout", {        Parent = Content,        Padding = UDim.new(0, 10),        HorizontalAlignment = Enum.HorizontalAlignment.Center    })    self.Window = {        Content = Content,        ScreenGui = ScreenGui    }    -- Кнопка закрытия/скрытия (крупная для мобильных)    local ToggleBtn = Create("TextButton", {        Parent = ScreenGui,        Size = UDim2.new(0.15, 0, 0.07, 0),        Position = UDim2.new(0.425, 0, 0.01, 0),        BackgroundColor3 = Color3.fromRGB(45, 45, 45),        Text = "МЕНЮ",        TextColor3 = Color3.fromRGB(255, 255, 255),        TextScaled = true    })    Create("UICorner", { Parent = ToggleBtn })    ToggleBtn.MouseButton1Click:Connect(function()        Main.Visible = not Main.Visible    end)    return selfend-- Элемент: Кнопкаfunction FluentMobile:AddButton(config)    local Title = config.Title or "Button"    local Callback = config.Callback or function() end    local BtnFrame = Create("TextButton", {        Parent = self.Window.Content,        Size = UDim2.new(0.95, 0, 0, 50), -- Фиксированная высота для удобства нажатия        BackgroundColor3 = Color3.fromRGB(45, 45, 45),        Text = Title,        TextColor3 = Color3.fromRGB(255, 255, 255),        TextSize = 18,        AutoButtonColor = true    })    Create("UICorner", { CornerRadius = UDim.new(0, 6), Parent = BtnFrame })    BtnFrame.MouseButton1Click:Connect(function()        Callback()    end)        return BtnFrameend-- Элемент: Переключатель (Toggle)function FluentMobile:AddToggle(id, config)    local Title = config.Title or "Toggle"    local Default = config.Default or false    local Callback = config.Callback or function() end    local ToggleFrame = Create("Frame", {        Parent = self.Window.Content,        Size = UDim2.new(0.95, 0, 0, 50),        BackgroundColor3 = Color3.fromRGB(40, 40, 40)    })    Create("UICorner", { Parent = ToggleFrame })    local Label = Create("TextLabel", {        Parent = ToggleFrame,        Text = Title,        Size = UDim2.new(0.7, 0, 1, 0),        Position = UDim2.new(0.05, 0, 0, 0),        BackgroundTransparency = 1,        TextColor3 = Color3.fromRGB(255, 255, 255),        TextXAlignment = Enum.TextXAlignment.Left,        TextSize = 16    })    local StatusIndicator = Create("Frame", {        Parent = ToggleFrame,        Size = UDim2.new(0.15, 0, 0.6, 0),        Position = UDim2.new(0.8, 0, 0.2, 0),        BackgroundColor3 = Default and Color3.fromRGB(0, 200, 100) or Color3.fromRGB(200, 50, 50)    })    Create("UICorner", { Parent = StatusIndicator })    local ClickBtn = Create("TextButton", {        Parent = ToggleFrame,        Size = UDim2.new(1, 0, 1, 0),        BackgroundTransparency = 1,        Text = ""    })    local state = Default    ClickBtn.MouseButton1Click:Connect(function()        state = not state        StatusIndicator.BackgroundColor3 = state and Color3.fromRGB(0, 200, 100) or Color3.fromRGB(200, 50, 50)        Callback(state)    end)end-- Элемент: Слайдер (Slider) - оптимизирован под Touchfunction FluentMobile:AddSlider(id, config)    local Title = config.Title or "Slider"    local Min = config.Min or 0    local Max = config.Max or 100    local Default = config.Default or Min    local Callback = config.Callback or function() end    local SliderFrame = Create("Frame", {        Parent = self.Window.Content,        Size = UDim2.new(0.95, 0, 0, 70),        BackgroundColor3 = Color3.fromRGB(40, 40, 40)    })    Create("UICorner", { Parent = SliderFrame })    local Label = Create("TextLabel", {        Parent = SliderFrame,        Text = Title .. ": " .. Default,        Size = UDim2.new(0.9, 0, 0.4, 0),        Position = UDim2.new(0.05, 0, 0.1, 0),        BackgroundTransparency = 1,        TextColor3 = Color3.fromRGB(255, 255, 255),        TextXAlignment = Enum.TextXAlignment.Left    })    local Bar = Create("Frame", {        Parent = SliderFrame,        Size = UDim2.new(0.9, 0, 0.15, 0),        Position = UDim2.new(0.05, 0, 0.65, 0),        BackgroundColor3 = Color3.fromRGB(60, 60, 60)    })    local Fill = Create("Frame", {        Parent = Bar,        Size = UDim2.new((Default - Min) / (Max - Min), 0, 1, 0),        BackgroundColor3 = Color3.fromRGB(0, 120, 255)    })    local function UpdateSlider(input)        local pos = math.clamp((input.Position.X - Bar.AbsolutePosition.X) / Bar.AbsoluteSize.X, 0, 1)        local val = math.floor(Min + (Max - Min) * pos)        Fill.Size = UDim2.new(pos, 0, 1, 0)        Label.Text = Title .. ": " .. val        Callback(val)    end    local dragging = false    SliderFrame.InputBegan:Connect(function(input)        if input.UserInputType == Enum.UserInputType.MouseButton1 or input.UserInputType == Enum.UserInputType.Touch then            dragging = true            UpdateSlider(input)        end    end)    UserInputService.InputChanged:Connect(function(input)        if dragging and (input.UserInputType == Enum.UserInputType.MouseMovement or input.UserInputType == Enum.UserInputType.Touch) then            UpdateSlider(input)        end    end)    UserInputService.InputEnded:Connect(function(input)        if input.UserInputType == Enum.UserInputType.MouseButton1 or input.UserInputType == Enum.UserInputType.Touch then            dragging = false        end    end)end-- Авто-обновление CanvasSize для прокруткиRunService.Heartbeat:Connect(function()    if FluentMobile.Window then        local content = FluentMobile.Window.Content        content.CanvasSize = UDim2.new(0, 0, 0, content.UIListLayout.AbsoluteContentSize.Y + 20)    endend)return FluentMobile